import React, {Component} from "react";
import Select from 'react-select';
import {useState} from "react";
import Navbar from "./components/navbar";
import './App.css';
import Counters from "./components/counters";
import Data from "./mock-data.json"

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];

const Option = (props) => {
  return (
    <div>
      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};

class App extends React.Component{

  state = {
    curData: [],
    selectedOption: null,
  }

  setQuery = (str) => {
    const tmpData = Data.filter(post => {
      if (str === '') {
        return post;
      } else if (post.Title.toLowerCase().includes(str.toLowerCase())) {
        return post;
      }
    });
    this.setState({curData: tmpData})
  }

  handleChange = (selectedOption) => {
    this.setState({ selectedOption }, () =>
      console.log(`Option selected:`, this.state.selectedOption)
    );
  };


  render() {
    // console.log(Data.map(post => post.gender));
    return (
      <React.Fragment>
        <div>
          <input id="searchWindow" placeholder="Enter Post Title"
                 onChange={event => this.setQuery(event.target.value)}/>
          {
            this.state.curData.map((post) => (
              <div className="box" key={post.id}>
                <p>{post.Title}</p>
                <p>{post.Rating}</p>
              </div>
            ))
          }

          <Select
            value={this.state.selectedOption}
            onChange={this.handleChange}
            options={options}
          />
        </div>


      </React.Fragment>
        );
  }
}
export default App;

