import React, {Component} from "react";
import Select from 'react-select';
import { components } from "react-select";
import {useState} from "react";
import Navbar from "./components/navbar";
import './App.css';
import Counters from "./components/counters";
import Data from "./mock-data.json"

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];

/**
 * Adding checkbox to dropdown selection bar
 *
 * reference: https://medium.com/geekculture/creating-multi-select-dropdown-with-checkbox-in-react-792ff2464ef3
 */
const Option = (props) => {
  return (
    <div>
      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};


class App extends React.Component{

  state = {
    curData: [],
    selectedOption: null,
  }

  setQuery = (str) => {
    const tmpData = Data.filter(post => {
      if (str === '') {
        return post;
      } else if (post.Title.toLowerCase().includes(str.toLowerCase())) {
        return post;
      }
    });
    this.setState({curData: tmpData})
  }

  handleChange = (selected) => {
    console.log(selected)
    this.setState({
      selectedOption: selected
    });
  };


  render() {
    // console.log(Data.map(post => post.gender));
    return (
      <React.Fragment>
        <div>
          <input id="searchWindow" placeholder="Enter Post Title"
                 onChange={event => this.setQuery(event.target.value)}/>
          {
            this.state.curData.map((post) => (
              <div className="box" key={post.id}>
                <p>{post.Title}</p>
                <p>{post.Rating}</p>
              </div>
            ))
          }

          <Select
            options={options}
            isMulti
            closeMenuOnSelect={false}
            hideSelectedOptions={false}
            components={{Option}}
            onChange={this.handleChange}
            allowSelectAll={true}
            value={this.state.optionSelected}
          />
        </div>


      </React.Fragment>
        );
  }
}
export default App;

