import React, {Component} from "react";
import {useState} from "react";
import Navbar from "./components/navbar";
import './App.css';
import Counters from "./components/counters";
import Data from "./mock-data.json"

var checkList = document.getElementById('list1');
var items = document.getElementById('items');
checkList.getElementsByClassName('anchor')[0].onclick = function (evt) {
  if (items.classList.contains('visible')){
    items.classList.remove('visible');
    items.style.display = "none";
  }

  else{
    items.classList.add('visible');
    items.style.display = "block";
  }


}

items.onblur = function(evt) {
  items.classList.remove('visible');
}


class App extends Component{

  state = {
    curData: [],
  }

  setQuery = (str) => {
    const tmpData = Data.filter(post => {
      if (str === '') {
        return post;
      } else if (post.Title.toLowerCase().includes(str.toLowerCase())) {
        return post;
      }
    });
    this.setState({curData: tmpData})
  }


  render() {
    // console.log(Data.map(post => post.gender));
    return (
      <React.Fragment>
        <div>
          <input id="searchWindow" placeholder="Enter Post Title"
                 onChange={event => this.setQuery(event.target.value)}/>
          {
            this.state.curData.map((post) => (
              <div className="box" key={post.id}>
                <p>{post.Title}</p>
                <p>{post.Rating}</p>
              </div>
            ))
          }
        </div>

        <div id="list1" className="dropdown-check-list" tabIndex="100">
          <span className="anchor">Select Fruits</span>
          <ul id="items" className="items">
            <li><input type="checkbox"/>Apple</li>
            <li><input type="checkbox"/>Orange</li>
            <li><input type="checkbox"/>Grapes</li>
            <li><input type="checkbox"/>Berry</li>
            <li><input type="checkbox"/>Mango</li>
            <li><input type="checkbox"/>Banana</li>
            <li><input type="checkbox"/>Tomato</li>
          </ul>
        </div>

      </React.Fragment>
        );
  }
}
export default App;

